mapper.select:
SELECT
round(infos.publishTime/10000) as orderTime,

FROM infos
WHERE info_type = #{type} and channel_id = #{channelId}
order by publish_time desc,rand()

limit #{startNo} , #{endNo}

SELECT user_comments.*,infos.*,channels.name as type
FROM infos RIGHT JOIN user_comments ON user_comments.info_id = infos.info_id
left join channels ON channels.channelId = infos.channel_id
left JOIN user ON user_comments.user_id = user.userId
WHERE user.userId = #{userId}

  limit #{offset},#{limit}

select * from infos
WHERE title like #{keyword}
ORDER BY updateTime
DESC LIMIT #{startNo},#{endNo}

SELECT title
FROM infos
WHERE info_id = #{infoId}
