mapper.select:
SELECT
round(infos.publishTime/10000) as orderTime,

FROM infos
WHERE infoType = #{type} and channelId = #{channelId}
order by publishTime desc,rand()

limit #{startNo} , #{endNo}

SELECT user_comments.*,infos.*,channels.name as type
FROM infos RIGHT JOIN user_comments ON user_comments.infoId = infos.infoId
left join channels ON channels.channelId = infos.channelId
left JOIN user ON user_comments.userId = user.userId
WHERE user.userId = #{userId}

  limit #{offset},#{limit}

select * from infos
WHERE title like #{keyword}
ORDER BY updateTime
DESC LIMIT #{startNo},#{endNo}

SELECT title
FROM infos
WHERE infoId = #{infoId}
