mapper.insert:

insert into user_withdrawal_state (id, userId, money, 
  status, create_date, bill_no
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{money,jdbcType=DECIMAL},
  #{status,jdbcType=TINYINT}, #{createDate,jdbcType=TIMESTAMP}, #{billNo,jdbcType=VARCHAR}
  )

mapper.select:
select w.userId,w.money,w.bill_no,w.status as auditStatus,u.status,u.nickName,u.telephone,g2.gold/g1.gold as apprenticeRate, w.create_date as createDate,u.last_visit_date as lastVisitDate, r.number as readNumber, s.apprenticeNum as apprenticeNum, p.channel_name as channelName, u.account_status as accountStatus, u.income_status as incomeStatus, u.present_status as presentStatus, u.speech_status as speechStatus from user_withdrawal_state as w LEFT JOIN user as u ON w.userId = u.userId LEFT JOIN (select count(0) as number,userId from user_read_record GROUP BY userId) as r ON u.userId = r.userId LEFT join (SELECT count(apprenticeId) as apprenticeNum, userId from user_share GROUP BY userId) as s ON u.userId = s.userId LEFT JOIN platform_channel as p ON u.channel_id = p.id LEFT JOIN (SELECT SUM(gold) as gold, userId FROM user_gold_record where source != 7 GROUP BY userId) as g1 ON w.userId = g1.userId LEFT JOIN (SELECT SUM(gold) as gold, userId FROM user_gold_record where source in (6,9,10) GROUP BY userId) as g2 ON w.userId = g2.userId WHERE w.status = 0
and (u.nickName LIKE CONCAT('%', #{keyWord}, '%') || u.telephone LIKE CONCAT('%', #{keyWord}, '%'))
and u.channel_id = #{channelId}
and date_format(w.create_date,'%M/%D/%Y') = #{createDate}
and w.status = #{status}
